const crypto = require('crypto');
const UP_WEBHOOK_SECRET = $env.UP_WEBHOOK_SECRET;

const input = $input.first();
const headers = input.json.headers || {};
const body = input.json.body;

const rawBody = JSON.stringify(body);

const signature = headers['x-up-authenticity-signature'] || '';

if (!signature) {
  return [{ json: body }];
}

const expectedSignature = crypto
  .createHmac('sha256', UP_WEBHOOK_SECRET)
  .update(rawBody)
  .digest('hex');

const isValid = crypto.timingSafeEqual(
  Buffer.from(signature, 'hex'),
  Buffer.from(expectedSignature, 'hex')
);

if (!isValid) {
  throw new Error('Invalid UP webhook signature — check UP_WEBHOOK_SECRET');
}

return [{ json: body }];

