  { type: 'up-category', pattern: 'music-and-streaming', field: 'up_category_id', category: 'fun', subcategory: 'streaming' },
  { type: 'up-category', pattern: 'events-and-gigs', field: 'up_category_id', category: 'fun', subcategory: 'events' },
  { type: 'up-category', pattern: 'hobbies', field: 'up_category_id', category: 'fun', subcategory: 'hobbies' }
];

const transaction = $input.first().json;
const description = (transaction.description || '').toUpperCase();
const upCategoryId = transaction.up_category_id || '';
const amountCents = transaction.amount_cents || 0;

let mainCategory = 'uncategorised';
let subCategory = 'unknown';

for (const rule of RULES) {
  const fieldValue = rule.field === 'description' ? description : upCategoryId;
  const pattern = rule.pattern.toUpperCase();

  if (rule.type === 'income') {
    if (amountCents > 0 && fieldValue.includes(pattern)) {
      mainCategory = rule.category;
      subCategory = rule.subcategory;
      break;
    }
  } else if (rule.type === 'merchant') {
    if (fieldValue.includes(pattern)) {
      mainCategory = rule.category;
      subCategory = rule.subcategory;
      break;
    }
  } else if (rule.type === 'up-category') {
    if (upCategoryId === rule.pattern) {
      mainCategory = rule.category;
      subCategory = rule.subcategory;
      break;
    }
  }
}

if (mainCategory === 'uncategorised' && amountCents > 200000) {
  mainCategory = 'income';
  subCategory = 'unknown';
}

return [{
  json: {
    ...transaction,
    main_category: mainCategory,
    sub_category: subCategory
  }
}];

const transaction = $input.first().json;

const prompt = `Given this Australian bank transaction:
- Description: "${transaction.description}"
- Amount: $${(Math.abs(transaction.amount_cents) / 100).toFixed(2)} ${transaction.amount_cents < 0 ? 'debit' : 'credit'}
- Raw text: "${transaction.raw_text || 'N/A'}"
- UP Category: "${transaction.up_category_id || 'none'}"

Categorise into exactly one main category: bills-fixed, groceries, transport, eating-out, shopping, health, fun, income

Also provide a subcategory (1-2 words, lowercase-hyphenated, e.g. "energy", "fuel", "restaurants").

Return ONLY valid JSON: {"category":"...","subcategory":"..."}`;

return [{
  json: {
    ...transaction,
    claude_prompt: prompt
  }
}];

const response = $input.first().json;
const transaction = $('Build Claude Prompt').first().json;

const content = response.content || [];
const textBlock = content.find(b => b.type === 'text');
let jsonText = (textBlock?.text || '').trim();

if (jsonText.startsWith('```json')) jsonText = jsonText.slice(7);
if (jsonText.startsWith('```')) jsonText = jsonText.slice(3);
if (jsonText.endsWith('```')) jsonText = jsonText.slice(0, -3);
jsonText = jsonText.trim();

let parsed;
try {
  parsed = JSON.parse(jsonText);
} catch (e) {
  parsed = { category: 'uncategorised', subcategory: 'unknown' };
}

const { claude_prompt, ...txnData } = transaction;

return [{
  json: {
    ...txnData,
    main_category: parsed.category || 'uncategorised',
    sub_category: parsed.subcategory || 'unknown'
  }
}];

const crypto = require('crypto');
const MJOLNIR_API_KEY = $env.MJOLNIR_API_KEY;
const MJOLNIR_WEBHOOK_SECRET = $env.MJOLNIR_WEBHOOK_SECRET;
const MJOLNIR_BASE_URL = $env.MJOLNIR_BASE_URL;

const body = JSON.stringify($input.first().json);
const timestamp = Date.now().toString();

const signature = crypto
  .createHmac('sha256', MJOLNIR_WEBHOOK_SECRET)
  .update(`${timestamp}.${body}`)
  .digest('hex');

return [{
  json: {
    url: `${MJOLNIR_BASE_URL}/api/up/transactions`,
    body: $input.first().json,
    headers: {
      'Content-Type': 'application/json',
      'X-Mjolnir-API-Key': MJOLNIR_API_KEY,
      'X-Mjolnir-Timestamp': timestamp,
      'X-Mjolnir-Signature': signature
    }
  }
}];

